INSERT INTO `content` VALUES ('157', '0', '移动办公能力支撑平台', '宁夏moa业务流程系统开发 内蒙moa系统维护 物联网moa系统维护', '1、宁夏业务流程宁夏政企进行测试，测试已根据反馈进行修改。\n2、配合内蒙政企进行内蒙moa系统维护。\n3、配合物联网政企进行物联网moa系统维护。', ' 1、解决宁夏业务流程系统处理单页面适配的问题。\n 2、宁夏业务流程系统公文附件屏蔽。\n 3、移动办公能力支撑平台二期初验会。', '周广志', '杨辰涛', '2018.3.30', '0', '无', '1、因两会期间封网，等封网结束后，安排上线。 宁夏政企正在封网期间加紧测试。', '交通行业中心', '3', '0', '8', '2018-03-16 09:39:13', '162');
