INSERT INTO `content` VALUES ('605', '0', '云化数字档案一期', '业务接口对接', '整理归档接口规范，协调省公司和集团集中业务系统进行归档接口改造，配合联调\n', '\"1、已整理完会计档案（集中财务系统）接口规范。\n2、修改云OA系统的数据归档接口标准。\n3、已整理完各省的统一数据归档接口。\n4、编写与合同系统的数据归档接口标准文档。\"\n', '徐亚波', '孟令侃', '2017年11月30日', '0', '无', '无', '医疗行业中心', '1', '0', '43', '2018-03-30 11:46:54', '168');
