INSERT INTO `content` VALUES ('614', '0', '会议助理平台一期工程', '会议助理系统迁移', '移动云三期环境即将到期，现需将会议助理系统从移动云三期迁移到移动云四期，包括管理员系统、手机客户端、网页客户端、短信服务等的迁移工作。', '前期测试已经完成，本周五（2018-03-30）晚8点起进行会议助理三期到四期的割接工作。\n', '崔磊', '宋炳乾、周帆、刘子砚、刘春桃、杜苏花', '2018-03-31', '0', '无', '无', '交通行业中心', '3', '0', '47', '2018-03-30 15:16:52', '168');
